import React from 'react'
import ParkCard from "../containers/ParkCard"
import {Button} from 'react-bootstrap'

class ParksList extends React.Component {
    state = {
        sorted: true,
        search: "",
        searchresult: []
    }

    renderParks = (parks) => {
        const defaultSort = this.state.sorted
        parks.sort(function(a,b) {
            if (defaultSort) {
                return b.likes - a.likes
            } else {
                return a.likes - b.likes
            }
        })
        return parks.map(park => <ParkCard key={park.id} park={park} />)
    } 
    parkFilter = () => {
        let filterList = ""
        if (this.state.searchresult === "") {
            filterList = this.props.parks
        }
        else {
            filterList = this.state.searchresult
        }
        return filterList
    }
    handleSort = () => {
        this.setState({
            sorted: !this.state.sorted
        })
    }
    handleOnChange = event => {
        this.setState({
            search: event.target.value
        })
    }
    handleOnSubmit = event => {
        event.preventDefault()
        this.setState({
            searchresult: this.props.parks.filter(park => park.name.toLowerCase() === this.state.search.toLowerCase())
        })
        this.setState({
            search: ""
        })
    }

    render() {
        return (
            <div>
                <h1>List of Top National Parks From {this.state.sorted ? "Most To Least Likes" : "Least To Most Likes"}</h1>
                <Button variant="outline-dark" className="sort-like" onClick={this.handleSort}>{this.state.sorted ? "Sort Least to Most Likes" : "Sort Most to Least Likes"}</Button>
                {this.renderParks(this.parkFilter())}
                <form onSubmit={this.handleOnSubmit}>
                    <input type="text" name="searchterm" value={this.state.search} onChange={this.handleOnChange} />
                    <input type="submit"></input>
                </form>
            </div>
        )
    }
}
export default ParksList
